import mx from "@mxgraph-app/mx";
import resources from "@mxgraph-app/resources";
import { DialogResizer } from "./resize";
import { DialogInit } from "./init";

type DialogOpts = {
  w?;
  h?;
  modal?;
  closable?;
  onClose?;
  noScroll?;
  transparent?;
  onResize?;
  ignoreBgClick?;
};

const { mxClient, mxPopupMenu } = mx;
const { IMAGE_PATH } = resources;

/**
 * Basic dialogs that are available in the viewer (print dialog).
 */
export class Dialog {
  /**
   *
   */
  static backdropColor = "white";

  documentMode: any;
  bg: any;
  dialogImg: any;
  onDialogClose: any;
  container: any;
  onResize: any;
  noScroll: any;
  elt: any;
  editorUi: any;

  w0: any;
  w: any;
  h0: any;

  dh: any;
  dx: any;
  left: any;
  top: any;
  h: any;
  div: any;

  dialogResizer?: DialogResizer;

  /**
   *
   */
  zIndex: any = mxPopupMenu.prototype.zIndex - 1;

  /**
   *
   */
  noColorImage = !mxClient.IS_SVG
    ? IMAGE_PATH + "/nocolor.png"
    : "data:image/png;base64,";

  /**
   *
   */
  closeImage = !mxClient.IS_SVG
    ? IMAGE_PATH + "/close.png"
    : "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAJAQMAAADaX5RTAAAABlBMVEV7mr3///+wksspAAAAAnRSTlP/AOW3MEoAAAAdSURBVAgdY9jXwCDDwNDRwHCwgeExmASygSL7GgB12QiqNHZZIwAAAABJRU5ErkJggg==";

  /**
   *
   */
  clearImage = !mxClient.IS_SVG
    ? IMAGE_PATH + "/clear.gif"
    : "data:image/gif;base64,";

  /**
   *
   */
  lockedImage = !mxClient.IS_SVG
    ? IMAGE_PATH + "/locked.png"
    : "data:image/png;base64,";

  /**
   *
   */
  unlockedImage = !mxClient.IS_SVG
    ? IMAGE_PATH + "/unlocked.png"
    : "data:image/png;base64,";

  /**
   * Removes the dialog from the DOM.
   */
  bgOpacity = 80;

  constructor(ui, element, opts: DialogOpts = {}) {
    this.initialize({ ui, element, ...opts });
    this.init();
  }

  getPosition(_left, _top, _w, _h): any {
    return {};
  }

  initialize(opts) {
    this.createDialogInit(opts).init();
  }

  createDialogInit(opts) {
    return new DialogInit(this, opts);
  }

  init() {}

  createDialogResizer() {
    return new DialogResizer(this);
  }

  resizeListener() {
    this.dialogResizer && this.dialogResizer.resizeListener();
  }
}
